mapper.select:
SELECT o.*,bs.student_name FROM original_record o LEFT JOIN b_student bs ON o.student_no = bs.student_no where 1=1
and DATE_FORMAT(access_date,'%Y-%m-%d') =DATE_FORMAT(#{date},'%Y-%m-%d')
limit #{currIndex},#{pageSize}

